fadingBubbles = function(t, l) {
    var i = this,
        n = 'fadingBubble',
        e = 'orange';
    t.selectAll(n)
        .data(l, JSON.stringify)
        .enter()
        .append('circle')
        .attr('class', n)
        .attr('cx', function(t) {
            return i.latLngToXY(t.latitude, t.longitude)[0];
        })
        .attr('cy', function(t) {
            return i.latLngToXY(t.latitude, t.longitude)[1];
        })
        .attr('r', function() {
            return 1;
        })
        .style('fill', function(t, l) {
            return i.options.fills && t.fillKey && i.options.fills[t.fillKey]
                ? i.options.fills[t.fillKey]
                : e;
        })
        .style('stroke', function(t, l) {
            return i.options.fills && t.fillKey && i.options.fills[t.fillKey]
                ? i.options.fills[t.fillKey]
                : e;
        })
        .transition()
        .duration(2e3)
        .ease(Math.sqrt)
        .attr('r', function(t) {
            return t.magnitude ? 20 * t.magnitude : 22;
        })
        .style('fill-opacity', 1e-6)
        .style('stroke-opacity', 1e-6)
        .remove();
};
